pragma solidity ^0.8.17;

import "./ERC20Upgrade.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract XSDToken is Initializable, ERC20Upgrade, UUPSUpgradeable {
    address public s_admin;

    modifier onlyOwner() {
        require(msg.sender == s_admin, "Ownable: caller is not the owner");
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() initializer {}

    function initialize(address initialOwner) public initializer {
        initializeERC20("XSD token", "XSD", 18);
        __UUPSUpgradeable_init();

        s_admin = initialOwner;
        _mint(msg.sender, 100000 * 10 ** 18);
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }
}
